def validate_config():
    """Validate that all required configuration is present"""
    missing_snowflake = [k for k, v in SNOWFLAKE_CONFIG.items() if not v or v.startswith('your_')]
    missing_aws = [k for k, v in AWS_CONFIG.items() if not v]
    
